import Header from './components/header';
import Texto from './components/Texto';
import TextoDestacado from './components/TextoDestacado';
import Apresentacao from './components/apresentacao';
import styled from 'styled-components';

// Styled component for the main section
const Inicio = styled.div`
  padding: 100px 0;
  height: 600px;
  width: 100%;
  background-color: #FF3F00;
  box-sizing: border-box; // Ensure padding is included in the width
`;

// Styled component for the overall app container
const AppGeral = styled.div`
  font-family: "Just Me Again Down Here", cursive;
  width: 100vw;
  height: 100vh;
  overflow-x: hidden; // Prevent horizontal scrolling
  /* Uncomment the following lines to add a background image */
  /* background-image: url('/src/images/fundo_place_holder.jpg');
  background-size: cover;
  background-repeat: no-repeat;
  background-position: center; */
`;

// Styled component for the construction text
const TextoConstrucao = styled.h2`
  font-family: "Just Me Again Down Here", cursive;
  font-weight: 400;
  font-style: normal;
  color: white;
  font-size: 55px;
  text-align: center;
  margin-top: 450px;
`;

function App() {
  return (
    <AppGeral>
      <Header />
      <Inicio>
        <TextoConstrucao>Introdução a robótica com projetos simples</TextoConstrucao>
      </Inicio>
      <Apresentacao />
    </AppGeral>
  );
}

export default App;
