import styled from "styled-components";
import GifRoboUm from '../../images/robo_MoveX.gif';
import Texto from "../Texto";

const GridProjetos = styled.div`
    margin-left: 0; 
    display: grid;
    flex-wrap: wrap; 
    justify-content: center;
    align-items: center;
    height: 100vh;
`;

const BoxProjetos = styled.a`
    background-image: url(${props => props.backgroundImage});
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
    border-radius: 7px;
    display: flex; 
    width: 540px;
    height: 540px;
    margin: 150px;
    text-decoration: none;
    color: white;
    transition: transform 0.3s ease; /* Adiciona transição suave */

    &:hover {
        transform: scale(1.1); /* Aumenta o tamanho quando o mouse está em cima */
    }
`;


function Projetos() {
    return (
        <GridProjetos>
            <BoxProjetos 
                href="/projetoUm" backgroundImage={GifRoboUm}>
            </BoxProjetos>
        </GridProjetos>
    );
}

export default Projetos;
