import styled from "styled-components";

export const DivTextoPageToda = styled.div`

    display: flex;
    flex-direction: column;
    gap: 5px;
    align-items: center;
    max-width: 800px; /* Define o limite de largura */
    width: 100%; /* Garante que a div ocupe 100% da largura disponível até o limite */
    margin 
`;
