import styled from "styled-components";
import TextoDestacado from "../TextoDestacado";
import Texto from "../Texto";
import Walee from "../../images/walle.png";
import PlaceHolder from "../../images/place_holder.png"
import breakpoints from "../styles/breakpoints";

const ContainerApresentacao = styled.div`
    width: 100vw;
    height: 100vh;
`;

const WaleeImg = styled.img`
    display: flex;
    padding: 0 150px;
    margin-left: auto;
`;

const Deladin = styled.div`
    display: flex;
    unicode-bidi: isolate;
    flex-direction: row;

    @media (max-width: ${breakpoints.mobile}) {
        flex-direction: column;
    }
`;

const IntroImg = styled.img`
    display: flex;
    padding: 0 130px;
    margin-right: 0px;
    margin-top: 50px;
    width: 724px;
    height: 599px;

    @media (max-width: ${breakpoints.mobile}) {
        width: 100%;
        height: auto;
        padding: 0;
        margin-top: 20px;
    }
`;

const TextoDiv = styled.div`
    display: flex;
    padding: 0 250px;
    margin-left: auto;
    margin-right: 0px;

    @media (max-width: ${breakpoints.mobile}) {
        padding: 0 20px;
        margin-left: 0;
        margin-right: 0;
    }
`;
