import styled from "styled-components";
import Texto from "../Texto";
import Imagem from '../../images/fundo_place_holder.jpg'

const Container = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  align-items: center;
  height: 100vh;
  width: 100vw;
  position: relative;
  background: #f9f9f9;
`;

const Placeholder = styled.div`
  width: 300px;
  height: 300px;
  background: #ccc;
  display: flex;
  justify-content: center;
  align-items: center;
  font-size: 24px;
  font-weight: bold;
  color: white;
  margin: 20px;
`;

const TextWrapper = styled.div`
  position: absolute;
  top: 50%;
  right: 15%;
  transform: translateY(-50%);
`;

const Text = styled.p`
  margin: 0;
  font-size: 14px;
  font-family: Arial, sans-serif;

  &:nth-child(1) {
    color: red;
    font-weight: bold;
  }

  &:nth-child(2) {
    color: black;
  }
`;

const Monkey = styled.img`
  position: absolute;
  top: 10px;
  right: 20px;
  width: 100px;
`;

const CutterWrapper = styled.div`
  position: absolute;
  bottom: 20px;
  right: 20px;
  display: flex;
  align-items: center;
  gap: 10px;
`;

const Cutter = styled.img`
  width: 50px;
`;

const DashedLine = styled.div`
  border-top: 2px dashed black;
  width: 100px;
`;

const App = () => {
  return (
    <Container>
      <Placeholder>PLACEHOLDER</Placeholder>
      <Monkey src={MonkeyImage} alt="Macaco" />
      <TextWrapper>
        <Text>Texto Texto Texto Texto Texto Texto Texto Texto</Text>
        <Text>Texto Texto Texto Texto Texto Texto Texto Texto</Text>
      </TextWrapper>
      <CutterWrapper>
        <DashedLine />
        <Cutter src={CutterImage} alt="Cortador" />
      </CutterWrapper>
    </Container>
  );
};

export default App;
